import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Error from '../error/Error';

const Article = () => {

    const {title} = useParams();

     //Temporary Storage of DB data
     const [articleData,setarticleData] = useState([]);

    //Matching title parameter
    const article = articleData.find(i=>i.title === title);

     //Backend Connection API fetch
     useEffect(()=>{
        fetchAPI();
        },[title]
      );

      async function fetchAPI(){
          const response = await fetch(`http://localhost:5000/api/article/${title}`);
          const result =await response.json();
          setarticleData(result);
      }
      if(!article) return <Error />
    return (
        <div>
           <h1>This is a {article.name} article</h1>
           <p>{article.description}</p>
        </div>
    );
};

export default Article;
