import React, { useState } from 'react';
import './SignUp.css';
import validation from './Validation';

const SignUp = () => {
    
//Storing form values
    const[formValues,setFormValues] = useState({username:"",email:"",password:""});

    //Manage Form Error Values
    const [formErrorValues,setFormErrorValues]= useState({})

    const handleSignup = (event)=>{
        //Destructuring:Technique to get specific values 
        const{name,value} = event.target;
        setFormValues({...formValues,[name]:value});
    }
    //Manage Form Refresh
    const handleSignUp = (event)=>{
      setFormErrorValues(validation(formValues));
    }

    return (
       
    <div class="main">  	
		<input type="checkbox" id="chk" aria-hidden="true" />

			<div className="signup">
				<form onSubmit={handleSignUp}>
					<label for="chk" aria-hidden="true">Sign up</label>
					<input type="text" name="username" placeholder="User name" required="" value={formValues.username} onChange={handleSignup} />
                    <p>{formErrorValues.username}</p>
					<input type="email" name="email" placeholder="Email" required="" value={formValues.email} onChange={handleSignup} />
                    <p>{formErrorValues.email}</p>
					<input type="password" name="password" placeholder="Password" required="" value={formValues.password} onChange={handleSignup} />
                    <p>{formErrorValues.password}</p>
					<button >Sign up</button>
				</form>
			</div>

			<div className='login'>
				<form>
					<label for="chk" aria-hidden="true">Login</label>
					<input type="email" name="email" placeholder="Email" required="" />
					<input type="password" name="pswd" placeholder="Password" required="" />
					<button>Login</button>
				</form>
			</div>
	</div>
        
    );
};

export default SignUp;
