import './SignUp.css';
import MyForm from '../reading/Reading';
import React, { useEffect, useState } from 'react';
import validation from './Validation';
import axios from 'axios';

function Login(props) {

	//Storing form values
    var [formValues,setFormValues] = MyForm({email:"",password:""});

	  //Manage Form Error Values
	  const [formErrorValues,setFormErrorValues]= useState({});

	  //Flag for form submission Status
	  const [isSubmit,setIsSubmit] = useState(false);


	  function handleLoginSubmit(event){
		event.preventDefault();
        setFormErrorValues(validation(formValues));
        setIsSubmit(true);
		axios.post("http://localhost:5001/api/signup",formValues).then(
            (response)=>{
                console.log(response);
            }
        )
	  }

    return (
		<div className='signupbody'>
			<div className='main'>
            	<div className='login'>
				<form onSubmit={handleLoginSubmit}>
					<label  aria-hidden="true">Login</label>
					<input type="email" name="email" placeholder="Email" required="" />
					<input type="password" name="pswd" placeholder="Password" required="" />
					<button>Login</button>
				</form>
			</div>
            
        </div>

		</div>
        
    );
}

export default Login;
