function validation(formValues){
    const errors ={};
    const regex = /^[^\s@]+@[^\s@]+\.[^\s@]{2,}$/i;
    if(!formValues.username){
        errors.username="Username is required";
    }
    if(!formValues.email){
        errors.email = "Email is required"
    }

}
export default validation;
